import { Component, Input } from '@angular/core';
import { Employee } from './employee.model';
import { EmployeeService } from '../employee-service';
import { LoggingService } from '../logging-service';

@Component({
  selector: 'app-employee',
  templateUrl: './employee.component.html',
  styleUrls: ['./employee.component.css'],
  providers: []
})
export class EmployeeComponent {

  public employees: Employee[] = [];

  constructor(private employeeService: EmployeeService,private loggingService: LoggingService) {}

  ngOnInit(){
    this.employees = this.employeeService.employees;
  }

  ngOnSet() {
    this.employees = this.employeeService.employees;
    //this.employeeService.updateEmployee(this.employees)
  }

  addEmployee(emp:Employee){
    this.employees.push(emp);
  }

  editEmployee(Employee){
    console.log("edit "+Employee.name);
    console.log(this.loggingService.logAdminLogin("edit"));
  }

  deleteEmployee(Employee){
    console.log("delete  "+Employee.name);
    console.log(this.loggingService.logAdminLogin("delete"));
  }

}
