  selector: 'app-employee',
  templateUrl: './employee.component.html',
  styleUrls: ['./employee.component.css']
})
export class EmployeeComponent {
  employees: Employee[] = [
    new Employee(1,"Aditya","adi@gmail.com","SDE1"),
    new Employee(2,"Aditya","adi@gmail.com","SDE1")

  ];

}
