import {Component} from '@angular/core'
import { NgModel } from '@angular/forms';

@Component({
    selector: 'app-admin',
    templateUrl: './admin.component.html',
    styleUrls: ['./admin.component.css']
})
export class AdminComponent {
    adminId: number = 1;
    adminUsername: string = "Aditya";
    serverName = "";
    allowNewAdmin: boolean = false;
    admins = ['Aditya','Onkar'];
    adminCreationStatu:string = "No admin created";
    constructor(){
        setTimeout(() => {
            this.allowNewAdmin = true;
        },2000);
    }
    adminName = "TestName";
    onCreateAdmin(adminName){
        this.adminCreationStatu = "New Admin Created "+this.adminName;
    }
    onUpdateAdmin(event: Event){
        //console.log(event);
        this.serverName = (<HTMLInputElement>event.target).value;
    }
    username:string = "";
    onSubmitUser(){
        this.username="";
    }
}
