import {Component, ElementRef, ViewChild} from '@angular/core'
import { NgModel } from '@angular/forms';
import { Employee } from '../employee/employee.model';
import { LoggingService } from '../logging-service';
import { AdminService } from '../admin-service';

@Component({
    selector: 'app-admin',
    templateUrl: './admin.component.html',
    styleUrls: ['./admin.component.css'],
    providers: []
})
export class AdminComponent {
    @ViewChild('userNameInput') username: ElementRef;
    @ViewChild('passwordInput') password: ElementRef;

    constructor(private loggingService: LoggingService,private adminSerivce:AdminService) {}

    isAdmin : boolean = false;

    validateAdmin() {
        const username = this.username.nativeElement.value;
        const password = this.password.nativeElement.value;
        this.isAdmin = this.adminSerivce.validateAdmin(username,password);
    }

    
}
