import { Component } from '@angular/core';
import { ActivatedRoute, Params } from '@angular/router';
import { EmployeeService } from 'src/app/employee-service';
import { Employee } from '../employee.model';

@Component({
  selector: 'app-employeeinfo',
  templateUrl: './employeeinfo.component.html',
  styleUrls: ['./employeeinfo.component.css']
})
export class EmployeeinfoComponent {
  constructor(private route: ActivatedRoute,private employeeService: EmployeeService){}
  getEmployee: {id: number}
  employee: Employee[] = [];

  ngOnInit(){
    this.getEmployee = {
      id: this.route.snapshot.params['id'],
    }

    this.route.params
      .subscribe(
        (params:Params) => {
          this.getEmployee.id = params['id'];
        }
      );

      this.employee = this.employeeService.getEmployeebyID(this.getEmployee.id);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Employee Information</title>
    <link href="https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css" rel="stylesheet">
</head>
<body class="bg-gray-100">
    <header class="bg-blue-500 p-4 text-white">
        <div class="container mx-auto">
            <h1 class="text-3xl font-semibold">Employee Information</h1>
        </div>
    </header>
    <main class="container mx-auto mt-8 p-4 bg-white rounded-lg shadow-md">
        <section>
            <h2 class="text-2xl font-semibold mb-4">Employee Details</h2>
            {{getEmployee.id}}
            <div *ngFor="let emp in employee" class="grid grid-cols-2 gap-4">
                <div>
                    <label class="block text-gray-700 text-sm font-medium">Name</label>
                    <p class="text-gray-900">{{emp.name}}</p>
                </div>
                <div>
                    <label class="block text-gray-700 text-sm font-medium">Email</label>
                    <p class="text-gray-900">{{emp.email}}</p>
                </div>
                <div>
                    <label class="block text-gray-700 text-sm font-medium">Department</label>
                    <p class="text-gray-900">{{emp.department}}</p>
                </div>
            </div>
        </section>
        <section class="mt-8">
            <h2 class="text-2xl font-semibold mb-4">Additional Information</h2>
            <p class="text-gray-700">This section can contain additional information about the employee, such as their role, responsibilities, and any other relevant details.</p>
        </section>
    </main>
    <footer class="bg-blue-500 p-4 text-white mt-8">
        <div class="container mx-auto">
            <p>&copy; 2023 Employee Information</p>
        </div>
    </footer>
</body>
</html>
